(6544, 167, 'titel_block_1', 'Berufsunfähigkeits- versicherung'),
(6545, 167, '_titel_block_1', 'field_5cceac700dc53'),
(6546, 167, 'subtitle_block_1', 'DIE BESTEN ANGEBOTE AUS EXPERTENHAND'),
(6547, 167, '_subtitle_block_1', 'field_5cceac809c49a'),
(6548, 167, 'text__block_1', '<span style=\"vertical-align: inherit;\">Wir behalten Kosten, Leistungen und Bedingungen stets im Auge und vergleichen routinemäßig Angebote miteinander. Wir bieten stets den besten Schutz und das beste Angebot auf dem deutschen Markt unseren Kunden an. Aber das ist nicht alles! E nge Beziehungen zu verschiedenen, namentlich haftenden Versicherungsgesellschaften sind ein wichtiger Punkt für viele unserer Kunden.</span>\r\n\r\n<strong><span style=\"vertical-align: inherit;\">Daher übernehmen wir Ihren ersten Monatsbeitrag. Als Dankeschön für Ihre Anfrage und als Willkommensgeschenk.</span></strong>'),
(6549, 167, '_text__block_1', 'field_5cceace99c49b'),
(6550, 167, 'text_foto_block1', 'Wir versichern- damit Sie Ihre Flügel ausbreiten können.'),
(6551, 167, '_text_foto_block1', 'field_5cd12fbccb432'),
(6552, 167, 'titel_block_2', 'Das \"Warum\"'),
(6553, 167, '_titel_block_2', 'field_5cceb14637b08'),
(6554, 167, 'subtitle_block_2', 'BU-EXPERTEN'),
(6555, 167, '_subtitle_block_2', 'field_5cceb15d37b09'),
(6556, 167, 'text__block_2', 'Vier Punkte die Ihnen verdeutlichen werden, warum Sie auf unserer Seite goldrichtig sind und was Sie von uns erwarten können.'),
(6557, 167, '_text__block_2', 'field_5cceb16f37b0a'),
(6558, 167, 'list_of_elements_0_title_element', 'Unabhängige Experten'),
(6559, 167, '_list_of_elements_0_title_element', 'field_5cceb1b72d65a'),
(6560, 167, 'list_of_elements_0_text_element', 'Wir sind ein buntes Team aus jungen und gestandenen freien Versicherungsmaklern. Unsere Expertise liegt im Bereich \"Vorsorge & Einkommensschutz\"'),
(6561, 167, '_list_of_elements_0_text_element', 'field_5cceb1dc2d65b'),
(6562, 167, 'list_of_elements', '4'),
(6563, 167, '_list_of_elements', 'field_5cceb1a52d659'),
(6564, 167, 'list_of_elements_1_title_element', 'Die besten Angebote'),
(6565, 167, '_list_of_elements_1_title_element', 'field_5cceb1b72d65a'),
(6566, 167, 'list_of_elements_1_text_element', 'Sie bekommen bei uns nur die besten Angebote, das beste Preis-Leistungs-Verhältnis und die höchste Expertise.'),
(6567, 167, '_list_of_elements_1_text_element', 'field_5cceb1dc2d65b'),
(6568, 167, 'list_of_elements_2_title_element', 'Viel erlebt, viele Erfahrungen gemacht'),
(6569, 167, '_list_of_elements_2_title_element', 'field_5cceb1b72d65a'),
(6570, 167, 'list_of_elements_2_text_element', 'Viele unserer langjährige Kunden fühlen sich gut beraten und versichert. Manche von Ihnen sind jedoch berufsunfähig geworden (viele Schlaganfälle, Arbeitsunfälle, psychische, aber auch Krebserkrankungen)\r\nBei solchen schweren Schicksalsschlägen ist es gut wenn man sich über Finanzen keine Sorgen machen muss.'),
(6571, 167, '_list_of_elements_2_text_element', 'field_5cceb1dc2d65b'),
(6572, 167, 'list_of_elements_3_title_element', 'Unser Serviceversprechen'),
(6573, 167, '_list_of_elements_3_title_element', 'field_5cceb1b72d65a'),
(6574, 167, 'list_of_elements_3_text_element', 'Für Ihre individuellen Anfragen halten wir uns bereit und sind flexibel. Darüber hinaus stehen wir unseren Kunden ein ganzes Versicherungsleben zur Seite. Wir helfen Ihnen auch im Leistungsfall, den Leistungsbogen fachgerecht auszufüllen und die Fragen passend zu beantworten.'),
(6575, 167, '_list_of_elements_3_text_element', 'field_5cceb1dc2d65b'),
(6576, 167, 'foto_galerie', ''),
(6577, 167, '_foto_galerie', 'field_5cceb7bae5511'),
(6578, 167, 'text_1_block_3', 'Die Ursachen für Berufsunfähigkeit sind überaus vielschichtig und diese kann im Grunde jeden treffen.'),
(6579, 167, '_text_1_block_3', 'field_5cceb81092c29'),
