INSERT INTO `wp_ex_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(6972, 169, 'text__block_1', '<span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Wir behalten Kosten, Leistungen und Bedingungen stets im Auge und vergleichen routinemäßig Angebote miteinander. </span><span style=\"vertical-align: inherit;\">Wir bieten stets den besten Schutz und das beste Angebot auf dem deutschen Markt unseren Kunden an. </span><span style=\"vertical-align: inherit;\">Aber das ist nicht alles! </span><span style=\"vertical-align: inherit;\">E nge Beziehungen zu verschiedenen, namentlich haftenden Versicherungsgesellschaften sind ein wichtiger Punkt für viele unserer Kunden.</span></span></span>\r\n\r\n<strong><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Daher übernehmen wir Ihren ersten Monatsbeitrag. </span><span style=\"vertical-align: inherit;\">Als Dankeschön für Ihre Anfrage und als Willkommensgeschenk.</span></span></span></strong>'),
(6973, 169, '_text__block_1', 'field_5cceace99c49b'),
(6974, 169, 'text_foto_block1', 'Wir versichern- damit Sie Ihre Flügel ausbreiten können.'),
