(7180, 170, 'titel_block_1', 'Berufsunfähigkeits- versicherung'),
(7181, 170, '_titel_block_1', 'field_5cceac700dc53'),
(7182, 170, 'subtitle_block_1', 'DIE BESTEN ANGEBOTE AUS EXPERTENHAND'),
(7183, 170, '_subtitle_block_1', 'field_5cceac809c49a'),
(7184, 170, 'text__block_1', '<span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Wir behalten Kosten, Leistungen und Bedingungen stets im Auge und vergleichen routinemäßig Angebote miteinander. </span><span style=\"vertical-align: inherit;\">Wir bieten stets den besten Schutz und das beste Angebot auf dem deutschen Markt unseren Kunden an. </span><span style=\"vertical-align: inherit;\">Aber das ist nicht alles! </span><span style=\"vertical-align: inherit;\">E nge Beziehungen zu verschiedenen, namentlich haftenden Versicherungsgesellschaften sind ein wichtiger Punkt für viele unserer Kunden.</span></span></span>\r\n\r\n<strong><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Daher übernehmen wir Ihren ersten Monatsbeitrag. </span><span style=\"vertical-align: inherit;\">Als Dankeschön für Ihre Anfrage und als Willkommensgeschenk.</span></span></span></strong>'),
(7185, 170, '_text__block_1', 'field_5cceace99c49b'),
(7186, 170, 'text_foto_block1', 'Wir versichern- damit Sie Ihre Flügel ausbreiten können.'),
(7187, 170, '_text_foto_block1', 'field_5cd12fbccb432'),
(7188, 170, 'titel_block_2', 'Das \"Warum\"'),
(7189, 170, '_titel_block_2', 'field_5cceb14637b08'),
(7190, 170, 'subtitle_block_2', 'BU-EXPERTEN'),
(7191, 170, '_subtitle_block_2', 'field_5cceb15d37b09'),
(7192, 170, 'text__block_2', 'Vier Punkte die Ihnen verdeutlichen werden, warum Sie auf unserer Seite goldrichtig sind und was Sie von uns erwarten können.'),
(7193, 170, '_text__block_2', 'field_5cceb16f37b0a'),
(7194, 170, 'list_of_elements_0_title_element', 'Unabhängige Experten'),
(7195, 170, '_list_of_elements_0_title_element', 'field_5cceb1b72d65a'),
(7196, 170, 'list_of_elements_0_text_element', 'Wir sind ein buntes Team aus jungen und gestandenen freien Versicherungsmaklern. Unsere Expertise liegt im Bereich \"Vorsorge & Einkommensschutz\"'),
(7197, 170, '_list_of_elements_0_text_element', 'field_5cceb1dc2d65b'),
