(7200, 170, 'list_of_elements_1_title_element', 'Die besten Angebote'),
(7201, 170, '_list_of_elements_1_title_element', 'field_5cceb1b72d65a'),
(7202, 170, 'list_of_elements_1_text_element', 'Sie bekommen bei uns nur die besten Angebote, das beste Preis-Leistungs-Verhältnis und die höchste Expertise.'),
(7203, 170, '_list_of_elements_1_text_element', 'field_5cceb1dc2d65b'),
(7204, 170, 'list_of_elements_2_title_element', 'Viel erlebt, viele Erfahrungen gemacht'),
(7205, 170, '_list_of_elements_2_title_element', 'field_5cceb1b72d65a'),
(7206, 170, 'list_of_elements_2_text_element', 'Viele unserer langjährige Kunden fühlen sich gut beraten und versichert. Manche von Ihnen sind jedoch berufsunfähig geworden (viele Schlaganfälle, Arbeitsunfälle, psychische, aber auch Krebserkrankungen)\r\nBei solchen schweren Schicksalsschlägen ist es gut wenn man sich über Finanzen keine Sorgen machen muss.'),
(7207, 170, '_list_of_elements_2_text_element', 'field_5cceb1dc2d65b'),
(7208, 170, 'list_of_elements_3_title_element', 'Unser Serviceversprechen'),
(7209, 170, '_list_of_elements_3_title_element', 'field_5cceb1b72d65a'),
(7210, 170, 'list_of_elements_3_text_element', 'Für Ihre individuellen Anfragen halten wir uns bereit und sind flexibel. Darüber hinaus stehen wir unseren Kunden ein ganzes Versicherungsleben zur Seite. Wir helfen Ihnen auch im Leistungsfall, den Leistungsbogen fachgerecht auszufüllen und die Fragen passend zu beantworten.'),
(7211, 170, '_list_of_elements_3_text_element', 'field_5cceb1dc2d65b'),
