(7604, 172, 'titel_block_1', 'Berufsunfähigkeits- versicherung'),
(7605, 172, '_titel_block_1', 'field_5cceac700dc53'),
(7606, 172, 'subtitle_block_1', 'DIE BESTEN ANGEBOTE AUS EXPERTENHAND'),
(7607, 172, '_subtitle_block_1', 'field_5cceac809c49a'),
(7608, 172, 'text__block_1', '<span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Wir behalten Kosten, Leistungen und Bedingungen stets im Auge und vergleichen routinemäßig Angebote miteinander. </span><span style=\"vertical-align: inherit;\">Wir bieten stets den besten Schutz und das beste Angebot auf dem deutschen Markt unseren Kunden an. </span><span style=\"vertical-align: inherit;\">Aber das ist nicht alles! </span><span style=\"vertical-align: inherit;\">E nge Beziehungen zu verschiedenen, namentlich haftenden Versicherungsgesellschaften sind ein wichtiger Punkt für viele unserer Kunden.</span></span></span>\r\n\r\n<strong><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Daher übernehmen wir Ihren ersten Monatsbeitrag. </span><span style=\"vertical-align: inherit;\">Als Dankeschön für Ihre Anfrage und als Willkommensgeschenk.</span></span></span></strong>'),
(7609, 172, '_text__block_1', 'field_5cceace99c49b'),
(7610, 172, 'text_foto_block1', 'Wir versichern- damit Sie Ihre Flügel ausbreiten können.'),
(7611, 172, '_text_foto_block1', 'field_5cd12fbccb432'),
(7612, 172, 'titel_block_2', 'Das \"Warum\"'),
(7613, 172, '_titel_block_2', 'field_5cceb14637b08'),
(7614, 172, 'subtitle_block_2', 'BU-EXPERTEN'),
(7615, 172, '_subtitle_block_2', 'field_5cceb15d37b09'),
(7616, 172, 'text__block_2', 'Vier Punkte die Ihnen verdeutlichen werden, warum Sie auf unserer Seite goldrichtig sind und was Sie von uns erwarten können.'),
(7617, 172, '_text__block_2', 'field_5cceb16f37b0a'),
(7618, 172, 'list_of_elements_0_title_element', 'Unabhängige Experten'),
(7619, 172, '_list_of_elements_0_title_element', 'field_5cceb1b72d65a'),
(7620, 172, 'list_of_elements_0_text_element', 'Wir sind ein buntes Team aus jungen und gestandenen freien Versicherungsmaklern. Unsere Expertise liegt im Bereich \"Vorsorge & Einkommensschutz\"'),
(7621, 172, '_list_of_elements_0_text_element', 'field_5cceb1dc2d65b'),
(7622, 172, 'list_of_elements', '4'),
(7623, 172, '_list_of_elements', 'field_5cceb1a52d659'),
(7624, 172, 'list_of_elements_1_title_element', 'Die besten Angebote'),
(7625, 172, '_list_of_elements_1_title_element', 'field_5cceb1b72d65a'),
(7626, 172, 'list_of_elements_1_text_element', 'Sie bekommen bei uns nur die besten Angebote, das beste Preis-Leistungs-Verhältnis und die höchste Expertise.'),
(7627, 172, '_list_of_elements_1_text_element', 'field_5cceb1dc2d65b'),
(7628, 172, 'list_of_elements_2_title_element', 'Viel erlebt, viele Erfahrungen gemacht'),
(7629, 172, '_list_of_elements_2_title_element', 'field_5cceb1b72d65a'),
(7630, 172, 'list_of_elements_2_text_element', 'Viele unserer langjährige Kunden fühlen sich gut beraten und versichert. Manche von Ihnen sind jedoch berufsunfähig geworden (viele Schlaganfälle, Arbeitsunfälle, psychische, aber auch Krebserkrankungen)\r\nBei solchen schweren Schicksalsschlägen ist es gut wenn man sich über Finanzen keine Sorgen machen muss.'),
(7631, 172, '_list_of_elements_2_text_element', 'field_5cceb1dc2d65b'),
(7632, 172, 'list_of_elements_3_title_element', 'Unser Serviceversprechen'),
(7633, 172, '_list_of_elements_3_title_element', 'field_5cceb1b72d65a'),
(7634, 172, 'list_of_elements_3_text_element', 'Für Ihre individuellen Anfragen halten wir uns bereit und sind flexibel. Darüber hinaus stehen wir unseren Kunden ein ganzes Versicherungsleben zur Seite. Wir helfen Ihnen auch im Leistungsfall, den Leistungsbogen fachgerecht auszufüllen und die Fragen passend zu beantworten.'),
(7635, 172, '_list_of_elements_3_text_element', 'field_5cceb1dc2d65b'),
(7636, 172, 'foto_galerie', ''),
(7637, 172, '_foto_galerie', 'field_5cceb7bae5511'),
(7638, 172, 'text_1_block_3', 'Die Ursachen für Berufsunfähigkeit sind überaus vielschichtig und diese kann im Grunde jeden treffen.'),
(7639, 172, '_text_1_block_3', 'field_5cceb81092c29'),
(7640, 172, 'text_2_block_3', 'Auch Berufs- oder Freizeitunfälle stellen für die individuelle Erwerbsfähigkeit ein nicht zu unterschätzendes Risiko dar.'),
(7641, 172, '_text_2_block_3', 'field_5cceb81792c2a'),
