(8454, 176, 'titel_block_1', 'Berufsunfähigkeits- versicherung'),
(8455, 176, '_titel_block_1', 'field_5cceac700dc53'),
(8456, 176, 'subtitle_block_1', 'DIE BESTEN ANGEBOTE AUS EXPERTENHAND'),
(8457, 176, '_subtitle_block_1', 'field_5cceac809c49a'),
(8458, 176, 'text__block_1', '<span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Wir behalten Kosten, Leistungen und Bedingungen stets im Auge und vergleichen routinemäßig Angebote miteinander. </span><span style=\"vertical-align: inherit;\">Wir bieten stets den besten Schutz und das beste Angebot auf dem deutschen Markt unseren Kunden an. </span><span style=\"vertical-align: inherit;\">Aber das ist nicht alles! </span><span style=\"vertical-align: inherit;\">E nge Beziehungen zu verschiedenen, namentlich haftenden Versicherungsgesellschaften sind ein wichtiger Punkt für viele unserer Kunden.</span></span></span>\r\n\r\n<strong><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Daher übernehmen wir Ihren ersten Monatsbeitrag. </span><span style=\"vertical-align: inherit;\">Als Dankeschön für Ihre Anfrage und als Willkommensgeschenk.</span></span></span></strong>'),
(8459, 176, '_text__block_1', 'field_5cceace99c49b'),
(8460, 176, 'text_foto_block1', 'Wir versichern- damit Sie Ihre Flügel ausbreiten können.'),
(8461, 176, '_text_foto_block1', 'field_5cd12fbccb432'),
(8462, 176, 'titel_block_2', 'Das \"Warum\"'),
(8463, 176, '_titel_block_2', 'field_5cceb14637b08'),
(8464, 176, 'subtitle_block_2', 'BU-EXPERTEN'),
(8465, 176, '_subtitle_block_2', 'field_5cceb15d37b09'),
(8466, 176, 'text__block_2', 'Vier Punkte die Ihnen verdeutlichen werden, warum Sie auf unserer Seite goldrichtig sind und was Sie von uns erwarten können.'),
(8467, 176, '_text__block_2', 'field_5cceb16f37b0a'),
(8468, 176, 'list_of_elements_0_title_element', 'Unabhängige Experten'),
(8469, 176, '_list_of_elements_0_title_element', 'field_5cceb1b72d65a'),
(8470, 176, 'list_of_elements_0_text_element', 'Wir sind ein buntes Team aus jungen und gestandenen freien Versicherungsmaklern. Unsere Expertise liegt im Bereich \"Vorsorge & Einkommensschutz\"'),
(8471, 176, '_list_of_elements_0_text_element', 'field_5cceb1dc2d65b'),
(8472, 176, 'list_of_elements', '4'),
(8473, 176, '_list_of_elements', 'field_5cceb1a52d659'),
(8474, 176, 'list_of_elements_1_title_element', 'Die besten Angebote'),
(8475, 176, '_list_of_elements_1_title_element', 'field_5cceb1b72d65a'),
(8476, 176, 'list_of_elements_1_text_element', 'Sie bekommen bei uns nur die besten Angebote, das beste Preis-Leistungs-Verhältnis und die höchste Expertise.'),
(8477, 176, '_list_of_elements_1_text_element', 'field_5cceb1dc2d65b'),
(8478, 176, 'list_of_elements_2_title_element', 'Viel erlebt, viele Erfahrungen gemacht'),
(8479, 176, '_list_of_elements_2_title_element', 'field_5cceb1b72d65a'),
(8480, 176, 'list_of_elements_2_text_element', 'Viele unserer langjährige Kunden fühlen sich gut beraten und versichert. Manche von Ihnen sind jedoch berufsunfähig geworden (viele Schlaganfälle, Arbeitsunfälle, psychische, aber auch Krebserkrankungen)\r\nBei solchen schweren Schicksalsschlägen ist es gut wenn man sich über Finanzen keine Sorgen machen muss.'),
(8481, 176, '_list_of_elements_2_text_element', 'field_5cceb1dc2d65b'),
(8482, 176, 'list_of_elements_3_title_element', 'Unser Serviceversprechen'),
(8483, 176, '_list_of_elements_3_title_element', 'field_5cceb1b72d65a'),
(8484, 176, 'list_of_elements_3_text_element', 'Für Ihre individuellen Anfragen halten wir uns bereit und sind flexibel. Darüber hinaus stehen wir unseren Kunden ein ganzes Versicherungsleben zur Seite. Wir helfen Ihnen auch im Leistungsfall, den Leistungsbogen fachgerecht auszufüllen und die Fragen passend zu beantworten.'),
(8485, 176, '_list_of_elements_3_text_element', 'field_5cceb1dc2d65b'),
(8486, 176, 'foto_galerie', ''),
(8487, 176, '_foto_galerie', 'field_5cceb7bae5511'),
