(9313, 180, '_subtitle_block_1', 'field_5cceac809c49a'),
(9314, 180, 'text__block_1', '<span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Wir behalten Kosten, Leistungen und Bedingungen stets im Auge und vergleichen routinemäßig Angebote miteinander. </span></span></span></span></span><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Wir bieten stets den besten Schutz und das beste Angebot auf dem deutschen Markt unseren Kunden an. </span></span></span></span></span><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Aber das ist nicht alles! </span></span></span></span></span><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">E nge Beziehungen zu verschiedenen, namentlich haftenden Versicherungsgesellschaften sind ein wichtiger Punkt für viele unserer Kunden.</span></span></span></span></span></span></span>\r\n\r\n<strong><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Daher übernehmen wir Ihren ersten Monatsbeitrag. </span></span></span></span></span><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Als Dankeschön für Ihre Anfrage und als Willkommensgeschenk.</span></span></span></span></span></span></span></strong>'),
(9315, 180, '_text__block_1', 'field_5cceace99c49b'),
(9316, 180, 'text_foto_block1', 'Wir versichern- damit Sie Ihre Flügel ausbreiten können.'),
(9317, 180, '_text_foto_block1', 'field_5cd12fbccb432'),
(9318, 180, 'titel_block_2', 'Das \"Warum\"'),
(9319, 180, '_titel_block_2', 'field_5cceb14637b08'),
(9320, 180, 'subtitle_block_2', 'BU-EXPERTEN'),
(9321, 180, '_subtitle_block_2', 'field_5cceb15d37b09'),
(9322, 180, 'text__block_2', 'Vier Punkte die Ihnen verdeutlichen werden, warum Sie auf unserer Seite goldrichtig sind und was Sie von uns erwarten können.'),
(9323, 180, '_text__block_2', 'field_5cceb16f37b0a'),
(9324, 180, 'list_of_elements_0_title_element', 'Unabhängige Experten'),
(9325, 180, '_list_of_elements_0_title_element', 'field_5cceb1b72d65a'),
(9326, 180, 'list_of_elements_0_text_element', 'Wir sind ein buntes Team aus jungen und gestandenen freien Versicherungsmaklern. Unsere Expertise liegt im Bereich \"Vorsorge & Einkommensschutz\"'),
(9327, 180, '_list_of_elements_0_text_element', 'field_5cceb1dc2d65b'),
(9328, 180, 'list_of_elements', '4'),
(9329, 180, '_list_of_elements', 'field_5cceb1a52d659'),
(9330, 180, 'list_of_elements_1_title_element', 'Die besten Angebote'),
(9331, 180, '_list_of_elements_1_title_element', 'field_5cceb1b72d65a'),
(9332, 180, 'list_of_elements_1_text_element', 'Sie bekommen bei uns nur die besten Angebote, das beste Preis-Leistungs-Verhältnis und die höchste Expertise.'),
(9333, 180, '_list_of_elements_1_text_element', 'field_5cceb1dc2d65b'),
(9334, 180, 'list_of_elements_2_title_element', 'Viel erlebt, viele Erfahrungen gemacht'),
(9335, 180, '_list_of_elements_2_title_element', 'field_5cceb1b72d65a'),
(9336, 180, 'list_of_elements_2_text_element', 'Viele unserer langjährige Kunden fühlen sich gut beraten und versichert. Manche von Ihnen sind jedoch berufsunfähig geworden (viele Schlaganfälle, Arbeitsunfälle, psychische, aber auch Krebserkrankungen)\r\nBei solchen schweren Schicksalsschlägen ist es gut wenn man sich über Finanzen keine Sorgen machen muss.'),
(9337, 180, '_list_of_elements_2_text_element', 'field_5cceb1dc2d65b'),
(9338, 180, 'list_of_elements_3_title_element', 'Unser Serviceversprechen'),
(9339, 180, '_list_of_elements_3_title_element', 'field_5cceb1b72d65a'),
(9340, 180, 'list_of_elements_3_text_element', 'Für Ihre individuellen Anfragen halten wir uns bereit und sind flexibel. Darüber hinaus stehen wir unseren Kunden ein ganzes Versicherungsleben zur Seite. Wir helfen Ihnen auch im Leistungsfall, den Leistungsbogen fachgerecht auszufüllen und die Fragen passend zu beantworten.'),
(9341, 180, '_list_of_elements_3_text_element', 'field_5cceb1dc2d65b'),
(9342, 180, 'foto_galerie', ''),
(9343, 180, '_foto_galerie', 'field_5cceb7bae5511'),
(9344, 180, 'text_1_block_3', 'Die Ursachen für Berufsunfähigkeit sind überaus vielschichtig und diese kann im Grunde jeden treffen.'),
(9345, 180, '_text_1_block_3', 'field_5cceb81092c29'),
(9346, 180, 'text_2_block_3', 'Auch Berufs- oder Freizeitunfälle stellen für die individuelle Erwerbsfähigkeit ein nicht zu unterschätzendes Risiko dar.'),
(9347, 180, '_text_2_block_3', 'field_5cceb81792c2a'),
