(10655, 195, '_list_of_elements', 'field_5cceb1a52d659'),
(10656, 195, 'list_of_elements_1_title_element', 'Die besten Angebote'),
(10657, 195, '_list_of_elements_1_title_element', 'field_5cceb1b72d65a'),
(10658, 195, 'list_of_elements_1_text_element', 'Sie bekommen bei uns nur die besten Angebote, das beste Preis-Leistungs-Verhältnis und die höchste Expertise.'),
(10659, 195, '_list_of_elements_1_text_element', 'field_5cceb1dc2d65b'),
(10660, 195, 'list_of_elements_2_title_element', 'Viel erlebt, viele Erfahrungen gemacht'),
(10661, 195, '_list_of_elements_2_title_element', 'field_5cceb1b72d65a'),
(10662, 195, 'list_of_elements_2_text_element', 'Viele unserer langjährige Kunden fühlen sich gut beraten und versichert. Manche von Ihnen sind jedoch berufsunfähig geworden (viele Schlaganfälle, Arbeitsunfälle, psychische, aber auch Krebserkrankungen)\r\nBei solchen schweren Schicksalsschlägen ist es gut wenn man sich über Finanzen keine Sorgen machen muss.'),
(10663, 195, '_list_of_elements_2_text_element', 'field_5cceb1dc2d65b'),
(10664, 195, 'list_of_elements_3_title_element', 'Unser Serviceversprechen'),
(10665, 195, '_list_of_elements_3_title_element', 'field_5cceb1b72d65a'),
(10666, 195, 'list_of_elements_3_text_element', 'Für Ihre individuellen Anfragen halten wir uns bereit und sind flexibel. Darüber hinaus stehen wir unseren Kunden ein ganzes Versicherungsleben zur Seite. Wir helfen Ihnen auch im Leistungsfall, den Leistungsbogen fachgerecht auszufüllen und die Fragen passend zu beantworten.'),
(10667, 195, '_list_of_elements_3_text_element', 'field_5cceb1dc2d65b'),
(10668, 195, 'foto_galerie', ''),
(10669, 195, '_foto_galerie', 'field_5cceb7bae5511'),
