(6979, 169, '_subtitle_block_2', 'field_5cceb15d37b09'),
(6980, 169, 'text__block_2', 'Vier Punkte die Ihnen verdeutlichen werden, warum Sie auf unserer Seite goldrichtig sind und was Sie von uns erwarten können.'),
(6981, 169, '_text__block_2', 'field_5cceb16f37b0a'),
(6982, 169, 'list_of_elements_0_title_element', 'Unabhängige Experten'),
(6983, 169, '_list_of_elements_0_title_element', 'field_5cceb1b72d65a'),
(6984, 169, 'list_of_elements_0_text_element', 'Wir sind ein buntes Team aus jungen und gestandenen freien Versicherungsmaklern. Unsere Expertise liegt im Bereich \"Vorsorge & Einkommensschutz\"'),
