(6987, 169, '_list_of_elements', 'field_5cceb1a52d659'),
(6988, 169, 'list_of_elements_1_title_element', 'Die besten Angebote'),
(6989, 169, '_list_of_elements_1_title_element', 'field_5cceb1b72d65a'),
(6990, 169, 'list_of_elements_1_text_element', 'Sie bekommen bei uns nur die besten Angebote, das beste Preis-Leistungs-Verhältnis und die höchste Expertise.'),
(6991, 169, '_list_of_elements_1_text_element', 'field_5cceb1dc2d65b'),
(6992, 169, 'list_of_elements_2_title_element', 'Viel erlebt, viele Erfahrungen gemacht'),
(6993, 169, '_list_of_elements_2_title_element', 'field_5cceb1b72d65a'),
(6994, 169, 'list_of_elements_2_text_element', 'Viele unserer langjährige Kunden fühlen sich gut beraten und versichert. Manche von Ihnen sind jedoch berufsunfähig geworden (viele Schlaganfälle, Arbeitsunfälle, psychische, aber auch Krebserkrankungen)\r\nBei solchen schweren Schicksalsschlägen ist es gut wenn man sich über Finanzen keine Sorgen machen muss.'),
(6995, 169, '_list_of_elements_2_text_element', 'field_5cceb1dc2d65b'),
(6996, 169, 'list_of_elements_3_title_element', 'Unser Serviceversprechen'),
(6997, 169, '_list_of_elements_3_title_element', 'field_5cceb1b72d65a'),
(6998, 169, 'list_of_elements_3_text_element', 'Für Ihre individuellen Anfragen halten wir uns bereit und sind flexibel. Darüber hinaus stehen wir unseren Kunden ein ganzes Versicherungsleben zur Seite. Wir helfen Ihnen auch im Leistungsfall, den Leistungsbogen fachgerecht auszufüllen und die Fragen passend zu beantworten.'),
(6999, 169, '_list_of_elements_3_text_element', 'field_5cceb1dc2d65b'),
