(7392, 171, 'titel_block_1', 'Berufsunfähigkeits- versicherung'),
(7393, 171, '_titel_block_1', 'field_5cceac700dc53'),
(7394, 171, 'subtitle_block_1', 'DIE BESTEN ANGEBOTE AUS EXPERTENHAND'),
(7395, 171, '_subtitle_block_1', 'field_5cceac809c49a'),
(7396, 171, 'text__block_1', '<span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Wir behalten Kosten, Leistungen und Bedingungen stets im Auge und vergleichen routinemäßig Angebote miteinander. </span><span style=\"vertical-align: inherit;\">Wir bieten stets den besten Schutz und das beste Angebot auf dem deutschen Markt unseren Kunden an. </span><span style=\"vertical-align: inherit;\">Aber das ist nicht alles! </span><span style=\"vertical-align: inherit;\">E nge Beziehungen zu verschiedenen, namentlich haftenden Versicherungsgesellschaften sind ein wichtiger Punkt für viele unserer Kunden.</span></span></span>\r\n\r\n<strong><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Daher übernehmen wir Ihren ersten Monatsbeitrag. </span><span style=\"vertical-align: inherit;\">Als Dankeschön für Ihre Anfrage und als Willkommensgeschenk.</span></span></span></strong>'),
(7397, 171, '_text__block_1', 'field_5cceace99c49b'),
(7398, 171, 'text_foto_block1', 'Wir versichern- damit Sie Ihre Flügel ausbreiten können.'),
(7399, 171, '_text_foto_block1', 'field_5cd12fbccb432'),
(7400, 171, 'titel_block_2', 'Das \"Warum\"'),
(7401, 171, '_titel_block_2', 'field_5cceb14637b08'),
(7402, 171, 'subtitle_block_2', 'BU-EXPERTEN'),
(7403, 171, '_subtitle_block_2', 'field_5cceb15d37b09'),
(7404, 171, 'text__block_2', 'Vier Punkte die Ihnen verdeutlichen werden, warum Sie auf unserer Seite goldrichtig sind und was Sie von uns erwarten können.'),
(7405, 171, '_text__block_2', 'field_5cceb16f37b0a'),
(7406, 171, 'list_of_elements_0_title_element', 'Unabhängige Experten'),
(7407, 171, '_list_of_elements_0_title_element', 'field_5cceb1b72d65a'),
(7408, 171, 'list_of_elements_0_text_element', 'Wir sind ein buntes Team aus jungen und gestandenen freien Versicherungsmaklern. Unsere Expertise liegt im Bereich \"Vorsorge & Einkommensschutz\"'),
