INSERT INTO `wp_ex_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(7470, 171, 'angebotsliste_1_titel_angebotsliste', 'Schritt 2'),
(7471, 171, '_angebotsliste_1_titel_angebotsliste', 'field_5cced34f6f5d0'),
(7472, 171, 'angebotsliste_1_textangebote_liste', 'Nachdem Sie das Kontaktformular entsprechend ausgefüllt haben, bekommen Sie von unserem erfahrenen Team direkt einen Anruf – hier legen wir Wert auf persönlichen Kontakt am Telefon. Während des Telefonats werden Ihnen unsere Berater noch die ein oder andere Frage stellen – im Anschluss erhalten Sie ein umfangreiches Formular per E-Mail mit vorteilhaften BU-Angeboten verschiedener Versicherer von uns zugesandt.'),
(7473, 171, '_angebotsliste_1_textangebote_liste', 'field_5cced3806f5d1'),
(7474, 171, 'angebotsliste_2_titel_angebotsliste', 'Schritt 3'),
(7475, 171, '_angebotsliste_2_titel_angebotsliste', 'field_5cced34f6f5d0'),
(7476, 171, 'angebotsliste_2_textangebote_liste', 'Ausfüllen der Antragsunterlagen und Abschluss des Versicherungsvertrags: Füllen Sie im Anschluss, die für die Eröffnung des Versicherungsvertrags, notwendigen Antragsunterlagen aus und lassen Sie uns diese direkt wieder zukommen. Wir kümmern uns zudem auch gern um die Kündigung eventueller Altverträge.'),
(7477, 171, '_angebotsliste_2_textangebote_liste', 'field_5cced3806f5d1'),
(7478, 171, 'angebotsliste', '3'),
(7479, 171, '_angebotsliste', 'field_5cced33c6f5cf'),
(7480, 171, 'titel_block_6', 'Die Kosten'),
(7481, 171, '_titel_block_6', 'field_5cced6254edf9'),
(7482, 171, 'subtitle_block_6', 'einer Berufsunfähigkeitsversicherung'),
(7483, 171, '_subtitle_block_6', 'field_5cced62f4edfa'),
(7484, 171, 'text__block_6', 'Mithilfe des kostenlosen und unverbindlichen Versicherungsrechners für Berufsunfähigkeitsversicherungen können Sie jetzt anhand der Angabe Ihres Alters sowie der gewünschten, monatlichen Wunschsumme im Schadenfall sowie durch die Angabe Ihres Berufs die Kosten für die Versicherungsbeiträge Ihrer individuellen Berufsunfähigkeitsversicherung berechnen lassen. Abhängig sind die monatlichen Versicherungsbeiträge für Ihre potentielle Berufsunfähigkeitsversicherung dabei vor allem von den Faktoren des von Ihnen gewünschten Auszahlungsbetrags im Schadenfall, von Ihrem Alter, Ihrem Beruf sowie von weiteren Gesundheitsfaktoren. Über den praktischen Kostenrechner können Sie zudem auch gleich unverbindliche Angebote über unser Portal anfordern.'),
