(7816, 173, 'titel_block_1', 'Berufsunfähigkeits- versicherung'),
(7817, 173, '_titel_block_1', 'field_5cceac700dc53'),
(7818, 173, 'subtitle_block_1', 'DIE BESTEN ANGEBOTE AUS EXPERTENHAND'),
(7819, 173, '_subtitle_block_1', 'field_5cceac809c49a'),
(7820, 173, 'text__block_1', '<span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Wir behalten Kosten, Leistungen und Bedingungen stets im Auge und vergleichen routinemäßig Angebote miteinander. </span><span style=\"vertical-align: inherit;\">Wir bieten stets den besten Schutz und das beste Angebot auf dem deutschen Markt unseren Kunden an. </span><span style=\"vertical-align: inherit;\">Aber das ist nicht alles! </span><span style=\"vertical-align: inherit;\">E nge Beziehungen zu verschiedenen, namentlich haftenden Versicherungsgesellschaften sind ein wichtiger Punkt für viele unserer Kunden.</span></span></span>\r\n\r\n<strong><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Daher übernehmen wir Ihren ersten Monatsbeitrag. </span><span style=\"vertical-align: inherit;\">Als Dankeschön für Ihre Anfrage und als Willkommensgeschenk.</span></span></span></strong>'),
(7821, 173, '_text__block_1', 'field_5cceace99c49b'),
(7822, 173, 'text_foto_block1', 'Wir versichern- damit Sie Ihre Flügel ausbreiten können.'),
(7823, 173, '_text_foto_block1', 'field_5cd12fbccb432'),
