(8032, 174, 'text__block_1', '<span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Wir behalten Kosten, Leistungen und Bedingungen stets im Auge und vergleichen routinemäßig Angebote miteinander. </span><span style=\"vertical-align: inherit;\">Wir bieten stets den besten Schutz und das beste Angebot auf dem deutschen Markt unseren Kunden an. </span><span style=\"vertical-align: inherit;\">Aber das ist nicht alles! </span><span style=\"vertical-align: inherit;\">E nge Beziehungen zu verschiedenen, namentlich haftenden Versicherungsgesellschaften sind ein wichtiger Punkt für viele unserer Kunden.</span></span></span>\r\n\r\n<strong><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Daher übernehmen wir Ihren ersten Monatsbeitrag. </span><span style=\"vertical-align: inherit;\">Als Dankeschön für Ihre Anfrage und als Willkommensgeschenk.</span></span></span></strong>'),
(8033, 174, '_text__block_1', 'field_5cceace99c49b'),
(8034, 174, 'text_foto_block1', 'Wir versichern- damit Sie Ihre Flügel ausbreiten können.'),
(8035, 174, '_text_foto_block1', 'field_5cd12fbccb432'),
(8036, 174, 'titel_block_2', 'Das \"Warum\"'),
(8037, 174, '_titel_block_2', 'field_5cceb14637b08'),
