(8240, 175, 'titel_block_1', 'Berufsunfähigkeits- versicherung'),
(8241, 175, '_titel_block_1', 'field_5cceac700dc53'),
(8242, 175, 'subtitle_block_1', 'DIE BESTEN ANGEBOTE AUS EXPERTENHAND'),
(8243, 175, '_subtitle_block_1', 'field_5cceac809c49a'),
(8244, 175, 'text__block_1', '<span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Wir behalten Kosten, Leistungen und Bedingungen stets im Auge und vergleichen routinemäßig Angebote miteinander. </span><span style=\"vertical-align: inherit;\">Wir bieten stets den besten Schutz und das beste Angebot auf dem deutschen Markt unseren Kunden an. </span><span style=\"vertical-align: inherit;\">Aber das ist nicht alles! </span><span style=\"vertical-align: inherit;\">E nge Beziehungen zu verschiedenen, namentlich haftenden Versicherungsgesellschaften sind ein wichtiger Punkt für viele unserer Kunden.</span></span></span>\r\n\r\n<strong><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Daher übernehmen wir Ihren ersten Monatsbeitrag. </span><span style=\"vertical-align: inherit;\">Als Dankeschön für Ihre Anfrage und als Willkommensgeschenk.</span></span></span></strong>'),
(8245, 175, '_text__block_1', 'field_5cceace99c49b'),
(8246, 175, 'text_foto_block1', 'Wir versichern- damit Sie Ihre Flügel ausbreiten können.'),
(8247, 175, '_text_foto_block1', 'field_5cd12fbccb432'),
(8248, 175, 'titel_block_2', 'Das \"Warum\"'),
(8249, 175, '_titel_block_2', 'field_5cceb14637b08'),
(8250, 175, 'subtitle_block_2', 'BU-EXPERTEN'),
(8251, 175, '_subtitle_block_2', 'field_5cceb15d37b09'),
(8252, 175, 'text__block_2', 'Vier Punkte die Ihnen verdeutlichen werden, warum Sie auf unserer Seite goldrichtig sind und was Sie von uns erwarten können.'),
(8253, 175, '_text__block_2', 'field_5cceb16f37b0a'),
(8254, 175, 'list_of_elements_0_title_element', 'Unabhängige Experten'),
(8255, 175, '_list_of_elements_0_title_element', 'field_5cceb1b72d65a'),
(8256, 175, 'list_of_elements_0_text_element', 'Wir sind ein buntes Team aus jungen und gestandenen freien Versicherungsmaklern. Unsere Expertise liegt im Bereich \"Vorsorge & Einkommensschutz\"'),
(8257, 175, '_list_of_elements_0_text_element', 'field_5cceb1dc2d65b'),
(8258, 175, 'list_of_elements', '4'),
(8259, 175, '_list_of_elements', 'field_5cceb1a52d659'),
(8260, 175, 'list_of_elements_1_title_element', 'Die besten Angebote'),
(8261, 175, '_list_of_elements_1_title_element', 'field_5cceb1b72d65a'),
(8262, 175, 'list_of_elements_1_text_element', 'Sie bekommen bei uns nur die besten Angebote, das beste Preis-Leistungs-Verhältnis und die höchste Expertise.'),
(8263, 175, '_list_of_elements_1_text_element', 'field_5cceb1dc2d65b'),
(8264, 175, 'list_of_elements_2_title_element', 'Viel erlebt, viele Erfahrungen gemacht'),
(8265, 175, '_list_of_elements_2_title_element', 'field_5cceb1b72d65a'),
(8266, 175, 'list_of_elements_2_text_element', 'Viele unserer langjährige Kunden fühlen sich gut beraten und versichert. Manche von Ihnen sind jedoch berufsunfähig geworden (viele Schlaganfälle, Arbeitsunfälle, psychische, aber auch Krebserkrankungen)\r\nBei solchen schweren Schicksalsschlägen ist es gut wenn man sich über Finanzen keine Sorgen machen muss.'),
(8267, 175, '_list_of_elements_2_text_element', 'field_5cceb1dc2d65b'),
(8268, 175, 'list_of_elements_3_title_element', 'Unser Serviceversprechen'),
(8269, 175, '_list_of_elements_3_title_element', 'field_5cceb1b72d65a'),
(8270, 175, 'list_of_elements_3_text_element', 'Für Ihre individuellen Anfragen halten wir uns bereit und sind flexibel. Darüber hinaus stehen wir unseren Kunden ein ganzes Versicherungsleben zur Seite. Wir helfen Ihnen auch im Leistungsfall, den Leistungsbogen fachgerecht auszufüllen und die Fragen passend zu beantworten.'),
(8271, 175, '_list_of_elements_3_text_element', 'field_5cceb1dc2d65b'),
(8272, 175, 'foto_galerie', ''),
(8273, 175, '_foto_galerie', 'field_5cceb7bae5511'),
(8274, 175, 'text_1_block_3', 'Die Ursachen für Berufsunfähigkeit sind überaus vielschichtig und diese kann im Grunde jeden treffen.'),
(8275, 175, '_text_1_block_3', 'field_5cceb81092c29'),
(8276, 175, 'text_2_block_3', 'Auch Berufs- oder Freizeitunfälle stellen für die individuelle Erwerbsfähigkeit ein nicht zu unterschätzendes Risiko dar.'),
(8277, 175, '_text_2_block_3', 'field_5cceb81792c2a'),
