(9115, 179, '_list_of_elements', 'field_5cceb1a52d659'),
(9116, 179, 'list_of_elements_1_title_element', 'Die besten Angebote'),
(9117, 179, '_list_of_elements_1_title_element', 'field_5cceb1b72d65a'),
(9118, 179, 'list_of_elements_1_text_element', 'Sie bekommen bei uns nur die besten Angebote, das beste Preis-Leistungs-Verhältnis und die höchste Expertise.'),
(9119, 179, '_list_of_elements_1_text_element', 'field_5cceb1dc2d65b'),
(9120, 179, 'list_of_elements_2_title_element', 'Viel erlebt, viele Erfahrungen gemacht'),
(9121, 179, '_list_of_elements_2_title_element', 'field_5cceb1b72d65a'),
(9122, 179, 'list_of_elements_2_text_element', 'Viele unserer langjährige Kunden fühlen sich gut beraten und versichert. Manche von Ihnen sind jedoch berufsunfähig geworden (viele Schlaganfälle, Arbeitsunfälle, psychische, aber auch Krebserkrankungen)\r\nBei solchen schweren Schicksalsschlägen ist es gut wenn man sich über Finanzen keine Sorgen machen muss.'),
(9123, 179, '_list_of_elements_2_text_element', 'field_5cceb1dc2d65b'),
(9124, 179, 'list_of_elements_3_title_element', 'Unser Serviceversprechen'),
(9125, 179, '_list_of_elements_3_title_element', 'field_5cceb1b72d65a'),
(9126, 179, 'list_of_elements_3_text_element', 'Für Ihre individuellen Anfragen halten wir uns bereit und sind flexibel. Darüber hinaus stehen wir unseren Kunden ein ganzes Versicherungsleben zur Seite. Wir helfen Ihnen auch im Leistungsfall, den Leistungsbogen fachgerecht auszufüllen und die Fragen passend zu beantworten.'),
(9127, 179, '_list_of_elements_3_text_element', 'field_5cceb1dc2d65b'),
(9128, 179, 'foto_galerie', ''),
(9129, 179, '_foto_galerie', 'field_5cceb7bae5511'),
(9130, 179, 'text_1_block_3', 'Die Ursachen für Berufsunfähigkeit sind überaus vielschichtig und diese kann im Grunde jeden treffen.'),
(9131, 179, '_text_1_block_3', 'field_5cceb81092c29'),
