(10877, 196, '_subtitle_block_2', 'field_5cceb15d37b09'),
(10878, 196, 'text__block_2', 'Vier Punkte die Ihnen verdeutlichen werden, warum Sie auf unserer Seite goldrichtig sind und was Sie von uns erwarten können.'),
(10879, 196, '_text__block_2', 'field_5cceb16f37b0a'),
(10880, 196, 'list_of_elements_0_title_element', 'Unabhängige Experten'),
(10881, 196, '_list_of_elements_0_title_element', 'field_5cceb1b72d65a'),
(10882, 196, 'list_of_elements_0_text_element', 'Wir sind ein buntes Team aus jungen und gestandenen freien Versicherungsmaklern. Unsere Expertise liegt im Bereich \"Vorsorge & Einkommensschutz\"'),
