(16220, 239, '_text__block_2', 'field_5cceb16f37b0a'),
(16221, 239, 'list_of_elements_0_title_element', 'Unabhängige Experten'),
(16222, 239, '_list_of_elements_0_title_element', 'field_5cceb1b72d65a'),
(16223, 239, 'list_of_elements_0_text_element', 'Wir sind ein buntes Team aus jungen und gestandenen freien Versicherungsmaklern. Unsere Expertise liegt im Bereich \"Vorsorge & Einkommensschutz\"'),
