(16923, 245, 'list_of_elements_1_title_element', 'Die besten Angebote'),
(16924, 245, '_list_of_elements_1_title_element', 'field_5cceb1b72d65a'),
(16925, 245, 'list_of_elements_1_text_element', 'Sie bekommen bei uns nur die besten Angebote, das beste Preis-Leistungs-Verhältnis und die höchste Expertise.'),
(16926, 245, '_list_of_elements_1_text_element', 'field_5cceb1dc2d65b'),
(16927, 245, 'list_of_elements_2_title_element', 'Viel erlebt, viele Erfahrungen gemacht'),
(16928, 245, '_list_of_elements_2_title_element', 'field_5cceb1b72d65a'),
(16929, 245, 'list_of_elements_2_text_element', 'Viele unserer langjährige Kunden fühlen sich gut beraten und versichert. Manche von Ihnen sind jedoch berufsunfähig geworden (viele Schlaganfälle, Arbeitsunfälle, psychische, aber auch Krebserkrankungen)\r\nBei solchen schweren Schicksalsschlägen ist es gut wenn man sich über Finanzen keine Sorgen machen muss.'),
(16930, 245, '_list_of_elements_2_text_element', 'field_5cceb1dc2d65b'),
(16931, 245, 'list_of_elements_3_title_element', 'Unser Serviceversprechen'),
(16932, 245, '_list_of_elements_3_title_element', 'field_5cceb1b72d65a'),
(16933, 245, 'list_of_elements_3_text_element', 'Für Ihre individuellen Anfragen halten wir uns bereit und sind flexibel. Darüber hinaus stehen wir unseren Kunden ein ganzes Versicherungsleben zur Seite. Wir helfen Ihnen auch im Leistungsfall, den Leistungsbogen fachgerecht auszufüllen und die Fragen passend zu beantworten.'),
(16934, 245, '_list_of_elements_3_text_element', 'field_5cceb1dc2d65b'),
(16935, 245, 'foto_galerie', ''),
(16936, 245, '_foto_galerie', 'field_5cceb7bae5511'),
