(17379, 249, '_subtitle_block_1', 'field_5cceac809c49a'),
(17380, 249, 'text__block_1', '<span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Wir behalten Kosten, Leistungen und Bedingungen stets im Auge und vergleichen routinemäßig Angebote miteinander. </span></span></span></span></span><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Wir bieten stets den besten Schutz und das beste Angebot auf dem deutschen Markt unseren Kunden an. </span></span></span></span></span><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Aber das ist nicht alles! </span></span></span></span></span><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">E nge Beziehungen zu verschiedenen, namentlich haftenden Versicherungsgesellschaften sind ein wichtiger Punkt für viele unserer Kunden.</span></span></span></span></span></span></span>\r\n\r\n<strong><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Daher übernehmen wir Ihren ersten Monatsbeitrag. </span></span></span></span></span><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Als Dankeschön für Ihre Anfrage und als Willkommensgeschenk.</span></span></span></span></span></span></span></strong>'),
(17381, 249, '_text__block_1', 'field_5cceace99c49b'),
(17382, 249, 'text_foto_block1', 'Wir versichern- damit Sie Ihre Flügel ausbreiten können.'),
(17383, 249, '_text_foto_block1', 'field_5cd12fbccb432'),
(17384, 249, 'titel_block_2', 'Das \"Warum\"'),
(17385, 249, '_titel_block_2', 'field_5cceb14637b08'),
