(18555, 257, '_list_of_elements_1_text_element', 'field_5cceb1dc2d65b'),
(18556, 257, 'list_of_elements_2_title_element', 'Viel erlebt, viele Erfahrungen gemacht'),
(18557, 257, '_list_of_elements_2_title_element', 'field_5cceb1b72d65a'),
(18558, 257, 'list_of_elements_2_text_element', 'Viele unserer langjährige Kunden fühlen sich gut beraten und versichert. Manche von Ihnen sind jedoch berufsunfähig geworden (viele Schlaganfälle, Arbeitsunfälle, psychische, aber auch Krebserkrankungen)\r\nBei solchen schweren Schicksalsschlägen ist es gut wenn man sich über Finanzen keine Sorgen machen muss.'),
(18559, 257, '_list_of_elements_2_text_element', 'field_5cceb1dc2d65b'),
(18560, 257, 'list_of_elements_3_title_element', 'Unser Serviceversprechen'),
(18561, 257, '_list_of_elements_3_title_element', 'field_5cceb1b72d65a'),
